import React, { useState, useEffect } from "react";
import { AdminPage, Panel } from "@admin/components/admin";
import Table from "@admin/components/common/Table";
import "./style.scss";
import {
  Button,
  DatePicker,
  Tooltip,
  Form,
  Col,
  Row,
  Input,
  Select,
  Icon,
  TimePicker,
  Checkbox,
  Modal,
  Upload,
} from "antd";
import commerciaNameProvider from "@data-access/commercial-name-provider";
import unitProvider from "@data-access/unit-provider";
import manufacturerProvider from "@data-access/manufacturer-provider";
import supplierProvider from "@data-access/supplier-provider";
import countryProvider from "@data-access/country-provider";
import deviceProvider from "@data-access/device-provider";
import statusProvider from "@data-access/status-provider";
import snackbar from "@utils/snackbar-utils";
const { Option } = Select;
const { confirm } = Modal;
function index(props) {
  let deviceId = props.match.params.id
  const [state, _setState] = useState({
    size: 10,
    page: 1,
    data: [],
    listNames: [],
    listUnits: [],
    listManufacturers: [],
    listSupplier: [],
    listCountry: [],
    listStatus: [],

    dmThietBiId: undefined,
    ten: "",
    soLuong: 1,
    serial: "",
    model: "",
    dmDonViTinhId: undefined,
    dmHangSanXuatId: undefined,
    dmDonViCungCapId: undefined,
    namSuDung: undefined,
    nuocSanXuatId: undefined,
    namSanXuat: undefined,
    namMua: undefined,
    giaNhap: "",
    dmtrangThaiId: undefined,
    donViSuDung: "",
    ghiChu: "",
    isTBPhuTro: false,

    dmThietBiPTId: undefined,
    dmThietBiPTName: "",
    soLuongPT: 1,
    serialPT: "",
    dmHangSanXuatPTId: undefined,
    dmHangSanXuatPTName: '',
    nuocSanXuatPTId: undefined,
    nuocSanXuatPTName: "",
    namMuaPT: undefined,
    trangThaiPT: undefined,
    trangThaiPTName: "",
    listTBPT: [],
    dataPT: [],
    isEditPT: false,
    keyEdit: undefined
  });

  const setState = (data = {}) => {
    _setState((state) => {
      return { ...state, ...data };
    });
  };
  const { getFieldDecorator } = props.form;

  useEffect(() => {
    onSearchStatus();
    onSearchName();
    onSearchUnit();
    onSearchManufacturer();
    onSearchSupplier();
    onSearchCountry()
    if (deviceId) {
      getDetail(deviceId);
    }
  }, []);
  const getDetail = (id) => {
    deviceProvider.getById(id).then(s => {
      if (s && s.code === 0) {
        let data = s.data
        setState({
          dmThietBiId: data.dmThietBiId,
          ten: data.ten,
          soLuong: data.soLuong,
          serial: data.serial,
          model: data.model,
          dmDonViTinhId: data.dmDonViTinhId,
          dmHangSanXuatId: data.dmHangSanXuatId,
          dmDonViCungCapId: data.dmDonViCungCapId,
          donViSuDung: data.donViSuDung,
          namSuDung: data.namSuDung,
          nuocSanXuatId: data.nuocSanXuatId,
          namSanXuat: data.namSanXuat,
          namMua: data.namMua,
          giaNhap: data.giaNhap,
          dmtrangThaiId: data.dmtrangThaiId,
          ghiChu: data.ghiChu,
          isTBPhuTro: data.thietBiPhuTro && data.thietBiPhuTro.length ? true : false
        })
      }
    }).catch(e => { })
  }
  const onSearchStatus = (page = 0, size = 10) => {
    statusProvider.search(page, size)
      .then((s) => {
        if (s.code == 0) {
          setState({
            listStatus: s.data,
          });
        }
      })
      .catch((e) => { });
  };
  const onSearchName = (page = 0, size = 10) => {
    commerciaNameProvider
      .search(page, size)
      .then((s) => {
        if (s.code == 0) {
          setState({
            listNames: s.data,
          });
        }
      })
      .catch((e) => { });
  };
  const onSearchUnit = (page = 0, size = 10) => {
    unitProvider
      .search(page, size)
      .then((s) => {
        if (s.code == 0) {
          setState({
            listUnits: s.data,
          });
        }
      })
      .catch((e) => { });
  };
  const onSearchManufacturer = (page = 0, size = 10) => {
    manufacturerProvider
      .search(page, size)
      .then((s) => {
        if (s.code == 0) {
          setState({
            listManufacturers: s.data,
          });
        }
      })
      .catch((e) => { });
  };
  const onSearchSupplier = (page = 0, size = 10) => {
    supplierProvider
      .search(page, size)
      .then((s) => {
        if (s.code == 0) {
          setState({
            listSupplier: s.data,
          });
        }
      })
      .catch((e) => { });
  };
  const onSearchCountry = (page = 0, size = 10) => {
    countryProvider
      .search(page, size)
      .then((s) => {
        if (s.code == 0) {
          setState({
            listCountry: s.data,
          });
        }
      })
      .catch((e) => { });
  };

  const onCheckDate = () => {
    let x = 1990;
    let check = [];
    do {
      check.push(x);
      x = x + 1;
    } while (x <= new Date().getFullYear());
    let c = check.map((item, index) => {
      return (
        <Option key={index} value={item}>
          {item}
        </Option>
      );
    });
    return c;
  };
  const onCheckUsedDate = () => {
    let x = 1990;
    let check = [];
    do {
      check.push(x);
      x = x + 1;
    } while (x <= new Date().getFullYear() + 10);
    let c = check.map((item, index) => {
      return (
        <Option key={index} value={item}>
          {item}
        </Option>
      );
    });
    return c;
  };
  const onSubmit = (e) => {
    e.preventDefault();
    let {
      dmThietBiId,
      ten,
      soLuong,
      serial,
      model,
      dmDonViTinhId,
      dmHangSanXuatId,
      dmDonViCungCapId,
      namSuDung,
      nuocSanXuatId,
      namSanXuat,
      namMua,
      giaNhap,
      dmtrangThaiId,
      ghiChu,
      donViSuDung,
      listTBPT
    } = state;
    let TBPhuTro = listTBPT && listTBPT.length ? listTBPT.map(item => {
      return {
        dmThietBiId: item.dmThietBiPTId,
        serial: item.serialPT,
        dmHangSanXuatId: item.dmHangSanXuatPTId,
        nuocSanXuatId: item.nuocSanXuatPTId,
        namMua: item.namMuaPT,
        dmTrangThaiId: item.trangThaiPT
      };
    }) : []

    let param = {
      dmThietBiId,
      ten,
      soLuong,
      serial,
      model,
      dmDonViTinhId,
      dmHangSanXuatId,
      dmDonViCungCapId,
      namSuDung,
      nuocSanXuatId,
      namSanXuat,
      namMua,
      giaNhap,
      dmtrangThaiId,
      ghiChu,
      donViSuDung,
      thietBiPhuTro: TBPhuTro
    };
    props.form.validateFields((err, values) => {
      if (!err) {
        confirm({
          title: "Xác nhận",
          content: `Bạn có muốn thêm ${soLuong} thiết bị ${ten} vào hệ thống không? `,
          okText: "Đồng ý",
          okType: "primary",
          cancelText: "Hủy",
          onOk() {
            deviceProvider
              .createOrEdit("", param)
              .then((s) => {
                if (s && s.code === 0) {
                  snackbar.show("Thêm thành công", "success");
                  props.history.push("/admin/device");
                } else if (s.code === 1000) {
                  snackbar.show("Thiếu thông tin", "danger");
                }
              })
              .catch((x) => { });
          },
          onCancel() {
          },
        });
      }
    });
  };
  const onSubmitPT = (e) => {
    e.preventDefault()
    let {
      dmThietBiPTId,
      dmThietBiPTName,
      soLuongPT,
      serialPT,
      dmHangSanXuatPTId,
      dmHangSanXuatPTName,
      nuocSanXuatPTId,
      nuocSanXuatPTName,
      namMuaPT,
      trangThaiPT,
      trangThaiPTName,
      listTBPT,
      isEditPT,
      keyEdit
    } = state
    if (!dmThietBiPTId) {
      snackbar.show("Vui lòng nhập tên thiết bị", "danger");
      return
    } else if (!soLuongPT) {
      snackbar.show("Vui lòng nhập số lượng", "danger");
      return
    } else if (!namMuaPT) {
      snackbar.show("Vui lòng chọn năm mua sắm", "danger");
      return
    }
    let param = {
      dmThietBiPTId,
      dmThietBiPTName,
      soLuongPT,
      serialPT,
      dmHangSanXuatPTId,
      dmHangSanXuatPTName,
      nuocSanXuatPTId,
      nuocSanXuatPTName,
      namMuaPT,
      trangThaiPT,
      trangThaiPTName
    }
    if (isEditPT) {
      let checkValue = listTBPT.filter((item, index) => keyEdit !== index)
      checkValue.push(param)
      setState({
        listTBPT: checkValue,
        dataPT: checkValue.map((item, index) => {
          return {
            key: index,
            col1: index,
            col2: item.dmThietBiPTName,
            col3: item.serialPT,
            col4: item.dmHangSanXuatPTName,
            col5: item.nuocSanXuatPTName,
            col6: item.namMuaPT,
            col7: item.trangThaiPTName,
            col8: index
          };
        }),
        dmThietBiPTId: undefined,
        dmThietBiPTName: "",
        soLuongPT: 1,
        serialPT: "",
        dmHangSanXuatPTId: undefined,
        dmHangSanXuatPTName: "",
        nuocSanXuatPTId: undefined,
        nuocSanXuatPTName: '',
        namMuaPT: undefined,
        trangThaiPT: undefined,
        trangThaiPTName: '',
        isEditPT: false,
        keyEdit: undefined
      })
    } else {
      setState({
        listTBPT: state.listTBPT.concat(param),
        dataPT: state.listTBPT.concat(param).map((item, index) => {
          return {
            key: index,
            col1: index,
            col2: item.dmThietBiPTName,
            col3: item.serialPT,
            col4: item.dmHangSanXuatPTName,
            col5: item.nuocSanXuatPTName,
            col6: item.namMuaPT,
            col7: item.trangThaiPTName,
            col8: index,
          };
        }),
        dmThietBiPTId: undefined,
        dmThietBiPTName: "",
        soLuongPT: 1,
        serialPT: "",
        dmHangSanXuatPTId: undefined,
        dmHangSanXuatPTName: "",
        nuocSanXuatPTId: undefined,
        nuocSanXuatPTName: "",
        namMuaPT: undefined,
        trangThaiPT: undefined,
        trangThaiPTName: "",
        isEditPT: false,
        keyEdit: undefined
      })
    }
  }
  const onDeleteItem = (key) => {
    confirm({
      title: "Xác nhận",
      content: `Bạn có muốn xóa thiết bị phụ trợ này?`,
      okText: "Xóa",
      okType: "danger",
      cancelText: "Hủy",
      onOk() {
        let checkValue = state.listTBPT.filter((item, index) => key !== index)
        setState({
          listTBPT: checkValue,
          dataPT: checkValue.map((item, index) => {
            return {
              key: index,
              col1: index,
              col2: item.dmThietBiPTName,
              col3: item.serialPT,
              col4: item.dmHangSanXuatPTName,
              col5: item.nuocSanXuatPTName,
              col6: item.namMuaPT,
              col7: item.trangThaiPTName,
              col8: index,
            }
          })
        })
      },
      onCancel() {
      },
    });

  }
  const editItemPT = (key) => {
    state.listTBPT.find((item, index) => {
      if (key === index) {
        setState({
          dmThietBiPTId: item.dmThietBiPTId,
          soLuongPT: item.soLuongPT,
          serialPT: item.serialPT,
          dmHangSanXuatPTId: item.dmHangSanXuatPTId,
          nuocSanXuatPTId: item.nuocSanXuatPTId,
          namMuaPT: item.namMuaPT,
          trangThaiPT: item.trangThaiPT,
          isEditPT: true,
          keyEdit: key
        })
      }
    })

  };
  return (
    <div>
      <AdminPage
        icon="subheader-icon fal fa-window"
        header="Thêm mới thiết bị"
        subheader="Nhập thông tin thiết bị cần thêm"
