        <Panel id="panel-list-site" title="THÔNG TIN THIẾT BỊ">
          <Form layout="vertical" hideRequiredMark>
            <div className="container div-combo9">
              <div className="row">
                <div className="col-md-4">
                  <Form.Item label="Tên thiết bị *" >
                    {getFieldDecorator("dmThietBiId", {
                      rules: [
