                    })(
                      <Input
                        placeholder="Nhập số lượng"
                        type="number"
                        onChange={(e) => {
                          setState({
                            soLuong: e.target.value,
                          });
                        }}
                        value={state.soLuong}
                      />
                    )}
                  </Form.Item>
                </div>
              </div>
              <div className="row">
                <div className="col-md-4">
                  <Form.Item
                    name="name"
                    label="Serial"
                    rules={[{ required: true, message: "Nhập serial" }]}
                  >
                    <Input
                      placeholder="Nhập serial"
