                      }
                    >
                      {onCheckDate()}
                    </Select>
                  </Form.Item>
                  <Form.Item className="sty-selcect">
                    <Select
                      value={state.trangThaiPT}
                      placeholder="Chọn trạng thái thiết bị"
                      onChange={(e, i) => {
                        setState({
                          trangThaiPT: e,
                          trangThaiPTName: i.props.children
                        });
                      }}
                      showSearch
                      filterOption={(input, option) =>
                        option.props.children
                          .toLowerCase()
                          .indexOf(input.toLowerCase()) >= 0
                      }
                    >
                      {state.listStatus && state.listStatus.length
                        ? state.listStatus.map((item, index) => {
                          return (
                            item && (
                              <Option key={index} value={item.id}>
                                {item.ten}
                              </Option>
                            )
                          );
                        })
                        : ""}
                    </Select>
                  </Form.Item>
                  <Button
                    type="primary"
                    onClick={(e) => onSubmitPT(e)}
                  >
                    <i className="fal fa-plus-circle" style={{ color: "#fff" }}></i>
                  </Button>
                </div>
              </div> : ""
            }


            <div
              className="style-button pull-right"
              style={{ display: "flex", justifyContent: "flex-end" }}
            >
              <Button
                type="primary"
                style={{ marginRight: 8 }}
                onClick={(e) => onSubmit(e)}
              >
                Thêm mới
              </Button>
              <Button type="danger" htmlType="submit">
                Reset
              </Button>
            </div>
          </Form>
        </Panel>
      </AdminPage>
    </div>
  );
}
export default Form.create()(index);
