import React, { useEffect } from "react";
import { BrowserRouter as Router, Link } from "react-router-dom";
// import { useRouter } from 'next/router'
function index(props) {
  // const router = useRouter();
  const getBreadcrumbs = () => {
    let url = (window.location.pathname || "").toLowerCase();
    let obj = [];
    switch (url) {
      case "/admin":
      case "/admin/dashboard":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/dashboard",
            name: "Dashboard",
          },
        ];
        break;
      case "/admin/manufacturer":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/admin/manufacturer",
            name: "Quản lý hãng sản xuất",
          },
        ];
        break;
      case "/admin/manufacturer/create":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/admin/manufacturer",
            name: "Quản lý hãng sản xuất",
          },
          {
            name: "Tạo mới",
          },
        ];
        break;
      case "/admin/device-type":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/admin/device-type",
            name: "Quản lý loại thiết bị",
          },
        ];
        break;
      case "/admin/device-type/create":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/admin/device-type",
            name: "Quản lý loại thiết bị",
          },
          {
            name: "Tạo mới",
          },
        ];
        break;
      case "/admin/device":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/admin/device",
            name: "Quản lý trang thiết bị",
          },
        ];
        break;
      case "/admin/device/create":
        obj = [
          {
            icon: "fal fa-home mr-1",
            url: "/admin",
            name: "Home",
          },
          {
            url: "/admin/device",
            name: "Quản lý trang thiết bị",
          },
          {
            url: "/admin/device/create",
            name: "Tạo mới",
          },
        ];
        break;
      default:
        if (url.indexOf("/admin/device/edit") == 0) {
          obj = [
            {
              icon: "fal fa-home mr-1",
              url: "/admin",
              name: "Home",
            },
            {
              url: "/admin/device",
              name: "Quản lý trang thiết bị",
            },
            {
              name: "Chỉnh sửa thiết bị",
            },
          ];
        } else if (url.indexOf("/admin/device/") == 0) {
          obj = [
            {
              icon: "fal fa-home mr-1",
              url: "/admin",
              name: "Home",
            },
            {
              url: "/admin/device",
              name: "Quản lý trang thiết bị",
            },
            {
              name: "Chi tiết thiết bị",
            },
          ];
        } else if (url.indexOf("/admin/manufacturer/") == 0) {
          obj = [
            {
              icon: "fal fa-home mr-1",
              url: "/admin",
              name: "Home",
            },
            {
              url: "/admin/device",
              name: "Quản lý nhà sản xuất",
            },
            {
              name: "Chỉnh sửa",
            },
          ];
        } else if (url.indexOf("/admin/device-type/") == 0) {
          obj = [
            {
              icon: "fal fa-home mr-1",
              url: "/admin",
              name: "Home",
            },
            {
              url: "/admin/device",
              name: "Quản lý loại thiết bị",
            },
            {
              name: "Chỉnh sửa",
            },
          ];
        }
        break;
    }
    return obj;
  };

  console.log(window.location.pathname);
  const breadCrumb = getBreadcrumbs();
  return (
    <ol className="breadcrumb bg-info-400">
      {breadCrumb.map((item, index) => {
        if (index < breadCrumb.length - 1)
          return (
            <li className="breadcrumb-item" key={index}>
              <Link to={item.url || "#"} className="text-white">
                {item.icon && <i className="fal fa-home mr-1"></i>}
                {item.name}
              </Link>
            </li>
          );
        return (
          <li className="breadcrumb-item active text-white" key={index}>
            {item.name}
          </li>
        );
      })}
    </ol>
  );
}
export default index;
