import React, { useState, useEffect } from "react";
import { AdminPage, Panel } from "@admin/components/admin";
import Table from "@admin/components/common/Table";
import SelectSize from "@admin/components/common/SelectSize";
import Pagination from "@admin/components/common/Pagination";
import { Select, Button, DatePicker, Tooltip, Modal } from "antd";
import deviceProvider from "@data-access/device-provider";
import DataContants from "@admin/components/config/data-contants";
import snackbar from "@utils/snackbar-utils";
import "./style.scss";
const { confirm } = Modal;
const { Option } = Select;
export default function index(props) {
  const [state, _setState] = useState({
    size: 10,
    page: 0,
    data: [],
  });
  const setState = (data = {}) => {
    _setState((state) => {
      return { ...state, ...data };
    });
  };
  const onSizeChange = (size) => {
    onSearch("size", size);
    setState({
      size: size
    })
  };
  const onPageChange = (page) => {
    onSearch("page", page - 1);
    setState({
      page: page
    })
  };
  useEffect(() => {
    onSearch(0, 10);
  }, []);
  const onSearch = (action, item) => {
    let page = action === "page" ? item : action === "size" ? 0 : state.page;
    let size = action === "size" ? item : state.size;
    let ma = action === "ma" ? item : state.ma;
    let ten = action === "ten" ? item : state.ten;
    let serial = action === "serial" ? item : state.serial;
    let model = action === "model" ? item : state.model;
    let dmHangSanXuat = action === "dmHangSanXuat" ? item : state.dmHangSanXuat;
    let namSanXuat = action === "namSanXuat" ? item : state.namSanXuat;
    let dmDonViTinh = action === "dmDonViTinh" ? item : state.dmDonViTinh;
    let namSuDung = action === "namSuDung" ? item : state.namSuDung;
    let trangThai = action === "trangThai" ? item : state.trangThai;
    // page = page ? page : state.page,
    // size = size ? size : state.size,
    // ma = ten ? ten : state.ma,
    // ten = ten ? ten : state.ten,
    // serial = serial ? serial : state.serial,
    // model = model ? model : state.model,
    // dmHangSanXuat = dmHangSanXuat ? dmHangSanXuat : state.dmHangSanXuat,
    // namSanXuat = namSanXuat ? namSanXuat : state.namSanXuat,
    // dmDonViTinh = dmDonViTinh ? dmDonViTinh : state.dmDonViTinh,
    // namSuDung = namSuDung ? namSuDung : state.namSuDung,
    // trangThai = trangThai ? trangThai : state.trangThai) => {
    // setState({
    //     page: page,
    //     size: size,
    //     ma: ma,
    //     ten: ten,
    //     serial: serial,
    //     model: model,
    //     dmHangSanXuat: dmHangSanXuat,
    //     namSanXuat: namSanXuat,
    //     dmDonViTinh: dmDonViTinh,
    //     namSuDung: namSuDung,
    //     trangThai: trangThai
    // });

    deviceProvider
      .search(
        page,
        size,
        ma,
        ten,
        serial,
        model,
        dmHangSanXuat,
        namSanXuat,
        dmDonViTinh,
        namSuDung,
        trangThai
      )
      .then((s) => {
        if (s.code == 0) {
          setState({
            total: s.totalElements,
            data:
              s.data && s.data.length
                ? s.data.map((item, index) => {
                  return {
                    key: index,
                    col1: page * size + index + 1,
                    // col2: item,
                    col3: item.ma,
                    col4: item.ten,
                    col5: item.serial,
                    col6: item.model,
                    col7: item.dmHangSanXuat,
                    col8: item.namSanXuat,
                    col9: item.dmDonViTinh,
                    col10: item.namSuDung,
                    col11: item.trangThai,
                    col12: item,
                  };
                })
                : [],
          });
        }
      })
      .catch((e) => { });
  };

  const exportExcel = (item) => () => { };

  const viewDetail = (item) => {
    props.history.push(`/admin/device/` + item.id);
  };
  const editItem = (item) => {
    props.history.push(`/admin/device/edit/` + item.id);
  };
  const checkStatus = (item) => {
    let status = DataContants.listStatus.filter((item) => {
      return parseInt(item.id) === Number(item);
    });
    if (status.length > 0) return status[0];
    return {};
  };
  const onDeleteItem = (item) => {
    return new Promise((resolve, reject) => {
      confirm({
        title: "Xác nhận",
        content: `Bạn có muốn xóa thiết bị ${item.ten}?`,
        okText: "Xóa",
        okType: "danger",
        cancelText: "Hủy",
        onOk() {
          deviceProvider
            .delete(item.id)
            .then((s) => {
              if (s && s.code === 0) {
                snackbar.show("Xóa thành công", "success");
                onSearch(0, 10);
              } else {
                snackbar.show("Xóa không thành công", "danger");
              }
            })
            .catch((e) => { });
        },
        onCancel() {
          reject();
        },
      });
    });
  };
  const addNew = () => {
    props.history.push("/admin/device/create");
  };
  return (
    <AdminPage
      icon="subheader-icon fal fa-window"
      header="Quản lý trang thiết bị"
      subheader="Danh sách trang thiết bị"
    >
      <Panel
        id="panel-list-site"
        title="DANH SÁCH TRANG THIẾT BỊ"
        toolbar={
          <div className="toolbar">
            <Button className="button">Xuất Excel</Button>
            <Button className="button" onClick={addNew}>
              Thêm mới
            </Button>
          </div>
        }
      >
        <Table
