                      }}
                    >
                      {DataContants.listStatus &&
                        DataContants.listStatus.length &&
                        DataContants.listStatus.map((option, index) => {
                          return (
                            <Option key={index} value={option.id}>
                              {option.name}
                            </Option>
                          );
                        })}
                    </Select>
                  </div>
                </div>
              ),
              width: 150,
              dataIndex: "col11",
              key: "col11",
              render: (item) => {
                return (
