import React, { useState, useEffect } from "react";
import { AdminPage, Panel } from "@admin/components/admin";
import Table from "@admin/components/common/Table";
import SelectSize from "@admin/components/common/SelectSize";
import Pagination from "@admin/components/common/Pagination";
import { Select, Button, DatePicker, Form, Input, Tooltip, Modal } from "antd";
import nameDeviceProvider from "@data-access/commercial-name-provider";
import deviceTypeProvider from "@data-access/device-type-provider";
import snackbar from "@utils/snackbar-utils";
const { confirm } = Modal;
function index(props) {
  const [state, _setState] = useState({
    size: 10,
    page: 0,
    data: [],
    listDeviceType: []
  });
  const [dataIndex, setDataIndex] = useState({});
  const setState = (data = {}) => {
    _setState((state) => {
      return { ...state, ...data };
    });
  };
  const onSizeChange = (size) => {
    onSearch("size", size);
    setState({
      size: size
    })
  };
  const onPageChange = (page) => {
    onSearch("page", page - 1);
    setState({
      page: page
    })
  };
  useEffect(() => {
    onSearch(0, 10);
    getDeviceType();
  }, []);
  const onSearch = (action, item) => {
    let page = action === "page" ? item : action === "size" ? 0 : state.page;
    let size = action === "size" ? item : state.size;
    let ma = action === "ma" ? item : state.maSearch;
    let ten = action === "ten" ? item : state.tenSearch;
    nameDeviceProvider
      .search(page, size, ma, ten)
      .then((s) => {
        if (s.code == 0) {
          setState({
            total: s.totalElements,
            data:
              s.data && s.data.length
                ? s.data.map((item, index) => {
                  return {
                    key: index,
                    col1: page * size + index + 1,
                    col2: item.ma,
                    col3: item.ten,
                    col4: item.dmLoaiThietBi && item.dmLoaiThietBi.ten,
                    col5: item.tenVietTat,
                    col6: new Date(item.createdAt).format("dd-MM-YYYY"),
                    col7: item,
                  };
                })
                : [],
          });
        }
      })
      .catch((e) => { });
  };
  const getDeviceType = () => {
    deviceTypeProvider.search(0, 9999).then(s => {
      if (s && s.code === 0) {
        setState({
          listDeviceType: s.data
        })
      }
    }).catch(e => {

    })
  }
  const onDeleteItem = (item) => {
    return new Promise((resolve, reject) => {
      confirm({
        title: "Xác nhận",
        content: `Bạn có muốn xóa tên thiết  bị ${item.ten}?`,
        okText: "Xóa",
        okType: "danger",
        cancelText: "Hủy",
        onOk() {
          nameDeviceProvider
            .delete(item.id)
            .then((s) => {
              if (s && s.code === 0) {
                snackbar.show("Xóa tên thiết  bị thành công", "success");
                onSearch(0, 10);
              } else {
                snackbar.show("Xóa tên thiết  bị không thành công", "danger");
              }
            })
            .catch((e) => { });
        },
        onCancel() {
          reject();
        },
      });
    });
  };
  const editItem = (item) => {
    if (item) {
      setDataIndex(item)
    } else {
      setDataIndex(item)
    }
  };
  const onClose = () => {
    setDataIndex({});
    setState({
      ma: "",
      ten: ""
    })
    props.history.push("/admin/name-device")
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    props.form.validateFields((err, values) => {
      if (!err) {
        create();
      }
    });
  };
  const { getFieldDecorator } = props.form;
  const create = () => {
    let ma = state.ma ? state.ma : dataIndex.ma
    let ten = state.ten ? state.ten : dataIndex.ten
    let tenVietTat = state.tenVietTat ? state.tenVietTat : dataIndex.tenVietTat
    let dmLoaiThietBiId = state.dmLoaiThietBiId ? state.dmLoaiThietBiId : dataIndex.dmLoaiThietBiId
    nameDeviceProvider.createOrEdit(dataIndex.id, ma, ten, tenVietTat, dmLoaiThietBiId).then((s) => {
      if (s && s.code === 0) {
        if (dataIndex.id) {
          snackbar.show("Cập nhật tên thiết  bị thành công", "success");
        } else {
          snackbar.show("Thêm mới tên thiết  bị thành công", "success");
        }
        onSearch(0, 10, "", "");
        onClose()
      } else {
        if (dataIndex.id) {
          snackbar.show("Cập nhật tên thiết  bị thất bại", "danger");
        } else {
          snackbar.show("Thêm mới tên thiết  bị thất bại", "danger");
        }
      }
    }).catch((e) => { });
  };
  return (
    <AdminPage
      icon="subheader-icon fal fa-window"
      header="Quản lý tên thiết  bị"
      subheader="Danh sách tên thiết  bị"
    >
      <div className="row">
        <div className="col-lg-8 ui-sortable sortable-grid">
          <Panel
            id="panel-list-site"
            title="DANH SÁCH TÊN THIẾT BỊ"
          // toolbar={
          //   <div className="toolbar">
          //     <Button className="button" onClick={() => editItem()}>
          //       Thêm mới
          //     </Button>
          //   </div>
          // }
          >
            <Table
              scroll={{ x: 800, y: 500 }}
              style={{ marginLeft: -10, marginRight: -10 }}
              className="custom"
              columns={[
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">STT</div>
                      <div className="addition-box"></div>
                    </div>
                  ),
                  width: 100,
                  dataIndex: "col1",
                  key: "col1",
                },
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">Mã</div>
                      <div className="addition-box">
                        <div className="search-box">
                          <img src={require("@images/icon/ic-search.png")} />
                          <input
                            value={state.maSearch}
                            onChange={(e) => onSearch("ma", e.target.value)}
                            placeholder="Tìm theo mã"
                          />
                        </div>
                      </div>
                    </div>
                  ),
                  width: 200,
                  dataIndex: "col2",
                  key: "col2",
                },
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">Tên gốc</div>
                      <div className="addition-box">
                        <div className="search-box">
                          <img src={require("@images/icon/ic-search.png")} />
                          <input
                            value={state.tenSearch}
                            onChange={(e) => onSearch("ten", e.target.value)}
                            placeholder="Tìm theo tên gốc"
                          />
                        </div>
                      </div>
                    </div>
                  ),
                  width: 200,
                  dataIndex: "col3",
                  key: "col3",
                },
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">Loại thiết bị</div>
                      <div className="addition-box">
                        <div className="search-box">
                          <img src={require("@images/icon/ic-search.png")} />
                          <input
                            value={state.maSearch}
                            onChange={(e) => onSearch("ma", e.target.value)}
                            placeholder="Tìm theo loại thiết bị"
                          />
                        </div>
                      </div>
                    </div>
                  ),
                  width: 200,
                  dataIndex: "col4",
                  key: "col4",
                },
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">Tên viết tắt</div>
                      <div className="addition-box">
                        <div className="search-box">
                          <img src={require("@images/icon/ic-search.png")} />
                          <input
                            value={state.maSearch}
                            onChange={(e) => onSearch("ma", e.target.value)}
                            placeholder="Tìm theo tên viết tắt"
                          />
                        </div>
                      </div>
                    </div>
                  ),
                  width: 200,
                  dataIndex: "col5",
                  key: "col5",
                },
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">Ngày tạo</div>
                      <div className="addition-box"></div>
                    </div>
                  ),
                  width: 200,
                  dataIndex: "col6",
                  key: "col6",
                },
                {
                  title: (
                    <div className="custome-header">
                      <div className="title-box">Tiện ích</div>
                      <div className="addition-box"></div>
                    </div>
                  ),
                  width: 90,
                  dataIndex: "col7",
                  key: "col7",
                  fixed: "right",
                  render: (item) => {
                    return (
                      <div className="col-action">
                        <Tooltip placement="topLeft" title={"Sửa"}>
                          <div>
                            <a
                              onClick={() => editItem(item)}
                              className="btn btn-info btn-icon waves-effect waves-themed"
                            >
                              <i className="fal fa-edit"></i>
                            </a>
                          </div>
                        </Tooltip>
                        <Tooltip placement="topLeft" title={"Xóa"}>
                          <div>
                            <a
                              onClick={() => onDeleteItem(item)}
                              className="btn btn-info btn-icon waves-effect waves-themed"
                            >
                              <i className="fal fa-trash-alt"></i>
                            </a>
                          </div>
                        </Tooltip>
                      </div>
                    );
                  },
                },
              ]}
              dataSource={state.data}
            ></Table>
            <div className="footer">
              <SelectSize value={state.size} selectItem={onSizeChange} />
              <Pagination
                onPageChange={onPageChange}
                page={state.page}
                size={state.size}
                total={state.total}
                style={{ flex: 1, justifyContent: "flex-end" }}
              />
            </div>
          </Panel>
        </div>
        <div className="col-lg-4  ui-sortable sortable-grid">
          <Panel
            title={dataIndex.id ? "Chỉnh sửa" : "Thêm mới"}
            id={"mgr-form-type"}
          // allowClose={false}
          // allowCollapse={false}
          >
            <Form layout="vertical" hideRequiredMark onSubmit={handleSubmit}>
              <div className="row">
                <div className="col">
                  <Form.Item label="Loại thiết  bị">
                    {getFieldDecorator("dataIndex.dmLoaiThietBiId", {
                      rules: [
                        {
                          required: true,
                          message: "Chọn loại thiết  bị",
                        },
                      ],
                      initialValue: dataIndex.dmLoaiThietBiId ? dataIndex.dmLoaiThietBiId : "",
                    })(
                      <Select
                        showSearch
                        filterOption={(input, option) =>
                          option.props.children
                            .toLowerCase()
                            .indexOf(input.toLowerCase()) >= 0
                        }
                        placeholder="Chọn loại thiết bị"
                        onChange={(e) => {
                          setState({
                            dmLoaiThietBiId: e,
                          });
                        }}
                      // value={state.dmLoaiThietBiId}
                      >
                        {state.listDeviceType && state.listDeviceType.length
                          ? state.listDeviceType.map((item, index) => {
                            return (
                              item && (
                                <Option key={index} value={item.id}>
                                  {item.ten}
                                </Option>
                              )
                            );
                          })
                          : ""}
                      </Select>
                    )}
                  </Form.Item>
                </div>
              </div>
              <div className="row">
                <div className="col">
                  <Form.Item label="Mã">
                    {getFieldDecorator("dataIndex.ma", {
                      rules: [
                        {
                          required: true,
                          message: "Nhập mã",
                        },
                      ],
                      initialValue: dataIndex.ma ? dataIndex.ma : "",
                    })(
                      <Input
                        onChange={(e) =>
                          setState({
                            ma: e.target.value,
                          })
                        }
                        placeholder="Nhập mã"
                      />
                    )}
                  </Form.Item>
                </div>
              </div>
              <div className="row">
                <div className="col">
                  <Form.Item label="Tên gốc">
                    {getFieldDecorator("dataIndex.ten", {
                      rules: [
                        {
                          required: true,
                          message: "Nhập tên gốc",
                        },
                      ],
                      initialValue: dataIndex.ten ? dataIndex.ten : "",
                    })(
                      <Input
                        onChange={(e) =>
                          setState({
                            ten: e.target.value,
                          })
                        }
                        placeholder="Nhập gốc"
                      />

                    )}
                  </Form.Item>
                </div>
              </div>
              <div className="row">
                <div className="col">
                  <Form.Item label="Tên viết tắt /ký hiệu">
                    {getFieldDecorator("dataIndex.tenVietTat", {
                      // rules: [
                      //   {
                      //     required: true,
                      //     message: "Nhập tên viết tắt /ký hiệu",
                      //   },
                      // ],
                      initialValue: dataIndex.tenVietTat ? dataIndex.tenVietTat : "",
                    })(
                      <Input
                        onChange={(e) =>
                          setState({
                            tenVietTat: e.target.value,
                          })
                        }
                        placeholder="Nhập tên viết tắt /ký hiệu"
                      />

                    )}
                  </Form.Item>
                </div>
              </div>
              <div
                style={{
                  width: "100%",
                  borderTop: "1px solid #e9e9e9",
                  padding: "16px 16px 0px",
                  background: "#fff",
                  textAlign: "right",
                }}
              >
                <Button onClick={() => onClose()} style={{ marginRight: 8 }}>
                  Hủy
                </Button>
                <Button type="primary" htmlType="submit" onClick={handleSubmit}>
                  {dataIndex.id ? "Lưu thay đổi" : "Tạo mới"}
                </Button>
              </div>
            </Form>
          </Panel>
        </div>
      </div>
    </AdminPage >
  );
}
export default Form.create()(index);
