import React, { useState, useEffect } from "react";
import { AdminPage, Panel } from "@admin/components/admin";
import Table from "@admin/components/common/Table";
import SelectSize from "@admin/components/common/SelectSize";
import Pagination from "@admin/components/common/Pagination";
import { Select, Button, DatePicker, Form, Input, Tooltip, Modal } from "antd";
import hangSanXuatProvider from "@data-access/manufacturer-provider";
import snackbar from "@utils/snackbar-utils";
const { confirm } = Modal;
function index(props) {
  const [state, _setState] = useState({
    size: 10,
    page: 0,
    data: [],
  });
  const [dataIndex, setDataIndex] = useState({});
  const setState = (data = {}) => {
    _setState((state) => {
      return { ...state, ...data };
    });
  };
  const onSizeChange = (size) => {
    onSearch("size", size);
    setState({
      size: size
    })
  };
  const onPageChange = (page) => {
    onSearch("page", page - 1);
    setState({
      page: page
    })
  };
  useEffect(() => {
    onSearch(0, 10);
  }, []);
  const onSearch = (action, item) => {
    let page = action === "page" ? item : action === "size" ? 0 : state.page;
    let size = action === "size" ? item : state.size;
    let ma = action === "ma" ? item : state.maSearch;
    let ten = action === "ten" ? item : state.tenSearch;
    hangSanXuatProvider
      .search(page, size, ma, ten)
      .then((s) => {
        if (s.code == 0) {
          setState({
            total: s.totalElements,
            data:
              s.data && s.data.length
                ? s.data.map((item, index) => {
                  return {
                    key: index,
                    col1: page * size + index + 1,
                    col2: item.ma,
                    col3: item.ten,
                    col4: new Date(item.createdAt).format("dd-MM-YYYY"),
                    col5: item,
                  };
                })
                : [],
          });
        }
      })
      .catch((e) => { });
  };
  const onDeleteItem = (item) => {
    return new Promise((resolve, reject) => {
      confirm({
        title: "Xác nhận",
        content: `Bạn có muốn xóa tài khoản ${item.ten}?`,
        okText: "Xóa",
        okType: "danger",
        cancelText: "Hủy",
        onOk() {
          hangSanXuatProvider
            .delete(item.id)
            .then((s) => {
              if (s && s.code === 0) {
                snackbar.show("Xóa tài khoản thành công", "success");
                onSearch(0, 10);
              } else {
                snackbar.show("Xóa tài khoản không thành công", "danger");
              }
            })
            .catch((e) => { });
        },
        onCancel() {
          reject();
        },
      });
    });
  };
  const editItem = (item) => {
    if (item) {
      props.history.push("/admin/user/" + item.id)
    } else {
      props.history.push("/admin/user/create")
    }
  };
  const onClose = () => {
    setDataIndex({});
    setState({
      ma: "",
      ten: ""
    })
    props.history.push("/admin/user")
  }
  
  return (
    <AdminPage
      icon="subheader-icon fal fa-window"
      header="Quản lý tài khoản"
      subheader="Danh sách tài khoản"
    >
      <Panel
        id="panel-list-site"
        title="DANH SÁCH TÀI KHOẢN"
        toolbar={
          <div className="toolbar">
            <Button className="button" onClick={() => editItem()}>
              Thêm mới
            </Button>
          </div>
        }
      >
        <Table
          scroll={{ x: 800, y: 500 }}
          style={{ marginLeft: -10, marginRight: -10 }}
          className="custom"
          columns={[
            {
              title: (
                <div className="custome-header">
                  <div className="title-box">STT</div>
                  <div className="addition-box"></div>
                </div>
              ),
              width: 100,
              dataIndex: "col1",
              key: "col1",
            },
            {
              title: (
                <div className="custome-header">
                  <div className="title-box">Mã tài khoản</div>
                  <div className="addition-box">
                    <div className="search-box">
                      <img src={require("@images/icon/ic-search.png")} />
                      <input
                        value={state.maSearch}
                        onChange={(e) => onSearch("ma", e.target.value)}
                        placeholder="Tìm theo mã tài khoản"
                      />
                    </div>
                  </div>
                </div>
              ),
              width: 200,
              dataIndex: "col2",
              key: "col2",
            },
            {
              title: (
                <div className="custome-header">
                  <div className="title-box">Tên tài khoản</div>
                  <div className="addition-box">
                    <div className="search-box">
                      <img src={require("@images/icon/ic-search.png")} />
                      <input
                        value={state.tenSearch}
                        onChange={(e) => onSearch("ten", e.target.value)}
                        placeholder="Tìm theo tên tài khoản"
                      />
                    </div>
                  </div>
                </div>
              ),
              width: 300,
              dataIndex: "col3",
              key: "col3",
            },
            {
              title: (
                <div className="custome-header">
                  <div className="title-box">Ngày tạo</div>
                  <div className="addition-box"></div>
                </div>
              ),
              width: 200,
              dataIndex: "col4",
              key: "col4",
            },
            {
              title: (
                <div className="custome-header">
                  <div className="title-box">Tiện ích</div>
                  <div className="addition-box"></div>
                </div>
              ),
              width: 90,
              dataIndex: "col5",
              key: "col5",
              fixed: "right",
              render: (item) => {
                return (
                  <div className="col-action">
                    <Tooltip placement="topLeft" title={"Sửa"}>
                      <div>
                        <a
                          onClick={() => editItem(item)}
                          className="btn btn-info btn-icon waves-effect waves-themed"
                        >
                          <i className="fal fa-edit"></i>
                        </a>
                      </div>
                    </Tooltip>
                    <Tooltip placement="topLeft" title={"Xóa"}>
                      <div>
                        <a
                          onClick={() => onDeleteItem(item)}
                          className="btn btn-info btn-icon waves-effect waves-themed"
                        >
                          <i className="fal fa-trash-alt"></i>
                        </a>
                      </div>
                    </Tooltip>
                  </div>
                );
              },
            },
          ]}
          dataSource={state.data}
        ></Table>
        <div className="footer">
          <SelectSize value={state.size} selectItem={onSizeChange} />
          <Pagination
            onPageChange={onPageChange}
            page={state.page}
            size={state.size}
            total={state.total}
            style={{ flex: 1, justifyContent: "flex-end" }}
          />
        </div>
      </Panel>
    </AdminPage>
  );
}
export default Form.create()(index);
