import React, { useState, useEffect } from "react";
import { AdminPage, Panel } from "@admin/components/admin";
import Table from "@admin/components/common/Table";
import { Select, Button, DatePicker, Tooltip } from "antd";
import DataContants from "@admin/components/config/data-contants";
import deviceProvider from "@data-access/device-provider";
import ListImage from "@admin/components/device/ListImage";
import "./style.scss";
const { Option } = Select;
export default function index(props) {
  const [state, _setState] = useState({
    data: [],
  });

  const setState = (data = {}) => {
    _setState((state) => {
      return { ...state, ...data };
    });
  };

  const closeDetail = () => {
    props.history.push(`/admin/device`);
  };
  useEffect(() => {
    let id = props.match.params.id;
    getDetail(id);
  }, []);
  const getDetail = (id) => {
    deviceProvider
      .getById(id)
      .then((s) => {
        if (s && s.code === 0) {
          setState({
            data: s.data,
          });
        }
      })
      .catch((e) => {});
  };
  const checkStatus = (item) => {
    let status = DataContants.listStatus.filter((item) => {
      return parseInt(item.id) === Number(item);
    });
    if (status.length > 0) return status[0];
    return {};
  };
  const { data } = state;
  return (
    <AdminPage
      icon="subheader-icon fal fa-window"
      header="Chi tiết thiết bị"
      subheader="Thông tin chi tiết thiết bị"
    >
      <Panel id="panel-list-site" title="THÔNG TIN THIẾT BI">
        <div className="row">
          <div className="col-sm-4">
            <div className="info-device">
              <h3 className="title">{data.ten}</h3>
              <div className="p-t-8">
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Tên thương mại:
                </strong>
                <label>{data.dmThietBi}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Mã:
                </strong>
                <label>{data.ma}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Serial Number:
                </strong>
                <label>{data.serial}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Model:
                </strong>
                <label>{data.model}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Đơn vị sử dụng:
                </strong>
                <label>{data.donViSuDung}</label>
              </div>
              {/* <div>
                                <strong className="width40" style={{ fontWeight: 600 }}> Tên cơ sở y tế</strong>
                                <label>1</label>
                            </div> */}
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Hãng sản xuất:
                </strong>
                <label>{data.dmHangSanXuat}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Nước sản xuất:
                </strong>
                <label>{data.nuocSanXuat}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Nguyên giá:
                </strong>
                <label>{data.giaNhap}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Đơn vị cung cấp:
                </strong>
                <label>{data.dmDonViCungCap}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Năm sản xuất:
                </strong>
                <label>{data.namSanXuat}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Năm mua sắm:
                </strong>
                <label>{data.namMua}</label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Năm sử dụng:
                </strong>
                <label>{data.namSuDung}</label>
              </div>
              {/* <div>
                                <strong className="width40" style={{ fontWeight: 600 }}> Tỉnh/TP</strong>
                                <label>1</label>
                            </div>
                            <div>
                                <strong className="width40" style={{ fontWeight: 600 }}> Quận/Huyện</strong>
                                <label>1</label>
                            </div>
                            <div>
                                <strong className="width40" style={{ fontWeight: 600 }}> Phường/Xã</strong>
                                <label>1</label>
                            </div> */}
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Trạng thái:
                </strong>
                <label>
                  {checkStatus(data.trangThai) &&
                    checkStatus(data.trangThai).name}
                </label>
              </div>
              <div>
                <strong className="width40" style={{ fontWeight: 600 }}>
                  {" "}
                  Ghi chú:
                </strong>
                <label>{data.ghiChu}</label>
              </div>
            </div>
            <div className="p-t-8">
              <button
                className="btn btn-info waves-effect waves-themed"
                onClick={() => {
                  closeDetail();
                }}
              >
                Trở về
              </button>
            </div>
          </div>
          <div className="col-sm-8">
            <div class="panel-content">
              <ul class="nav nav-tabs" role="tablist">
                <li class="nav-item">
                  <a
                    class="nav-link"
                    data-toggle="tab"
                    href="#tab_borders_icons-1"
                    role="tab"
                    aria-selected="false"
                  >
                    <i class="fal fa-home mr-1"></i> Thiết bị đính kèm
                  </a>
                </li>
                <li class="nav-item">
                  <a
                    class="nav-link active"
                    data-toggle="tab"
                    href="#tab_borders_icons-2"
                    role="tab"
                    aria-selected="true"
                  >
                    <i class="fal fa-file-image"></i> HÌnh Ảnh
                  </a>
                </li>
                <li class="nav-item">
                  <a
                    class="nav-link"
                    data-toggle="tab"
                    href="#tab_borders_icons-3"
                    role="tab"
                  >
                    <i class="fal fa-newspaper"></i> Tài liệu
                  </a>
                </li>
              </ul>
              <div class="tab-content border border-top-0 p-3">
                <div
                  class="tab-pane fade"
                  id="tab_borders_icons-1"
                  role="tabpanel"
                >
                  <Table
                    scroll={{ x: 200, y: 500 }}
                    style={{ marginLeft: -10, marginRight: -10 }}
                    className="custom"
                    columns={[
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">STT</div>
                            <div className="addition-box"></div>
                          </div>
                        ),
                        width: 70,
                        dataIndex: "col1",
                        key: "col1",
                      },
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">Mã thiết bị</div>
                            <div className="addition-box">
                              {/* <div className="search-box">
                                                                <img src={require("@images/icon/ic-search.png")} />
                                                                <input
                                                                    value={state.ma}
                                                                    onChange={(e) =>
                                                                        onSearch("", "", e.target.value)
                                                                    }
                                                                    placeholder="Tìm theo mã thiết bị"
                                                                />
                                                            </div> */}
                            </div>
                          </div>
                        ),
                        width: 150,
                        dataIndex: "col3",
                        key: "col3",
                      },
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">Tên thiết bị</div>
                            <div className="addition-box">
                              {/* <div className="search-box">
                                                                <img src={require("@images/icon/ic-search.png")} />
                                                                <input
                                                                    value={state.ten}
                                                                    onChange={(e) =>
                                                                        onSearch("", "", "", e.target.value)
                                                                    }
                                                                    placeholder="Tìm theo tên thiết bị"
                                                                />
                                                            </div> */}
                            </div>
                          </div>
                        ),
                        width: 150,
                        dataIndex: "col4",
                        key: "col4",
                      },
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">Serial</div>
                            <div className="addition-box">
                              {/* <div className="search-box">
                                                                <img src={require("@images/icon/ic-search.png")} />
                                                                <input
                                                                    value={state.serial}
                                                                    onChange={(e) =>
                                                                        onSearch("", "", "", "", e.target.value)
                                                                    }
                                                                    placeholder="Tìm theo số máy"
                                                                />
                                                            </div> */}
                            </div>
                          </div>
                        ),
                        width: 150,
                        dataIndex: "col5",
                        key: "col5",
                      },
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">Hãng sản xuất</div>
                            <div className="addition-box">
                              {/* <div className="search-box">
                                                                <img src={require("@images/icon/ic-search.png")} />
                                                                <input
                                                                    value={state.model}
                                                                    onChange={(e) =>
                                                                        onSearch("", "", "", "", "", e.target.value)
                                                                    }
                                                                    placeholder="Tìm theo hãng sản xuất"
                                                                />
                                                            </div> */}
                            </div>
                          </div>
                        ),
                        width: 200,
                        dataIndex: "col6",
                        key: "col6",
                      },
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">Nước sản xuất</div>
                            <div className="addition-box">
                              {/* <div className="search-box">
                                                                <img src={require("@images/icon/ic-search.png")} />
                                                                <input
                                                                    value={state.dmHangSanXuat}
                                                                    onChange={(e) =>
                                                                        onSearch("", "", "", "", "", "", e.target.value)
                                                                    }
                                                                    placeholder="Tìm theo nước sản xuất"
                                                                />
                                                            </div> */}
                            </div>
                          </div>
                        ),
                        width: 200,
                        dataIndex: "col7",
                        key: "col7",
                      },
                      {
                        title: (
                          <div className="custome-header">
                            <div className="title-box">Năm sản xuất</div>
                            <div className="addition-box">
                              {/* <div className="search-box">
                                                                <img src={require("@images/icon/ic-search.png")} />
                                                                <input
                                                                    value={state.namSanXuat}
                                                                    onChange={(e) =>
                                                                        onSearch("", "", "", "", "", "", "", e.target.value)
                                                                    }
                                                                    placeholder="Tìm theo năm sản xuất"
                                                                />
                                                            </div> */}
                            </div>
                          </div>
